import React, {useState} from "react";
import { Switch, Route } from "react-router-dom"
import Sidebar from "./sidebar";
import Home from "./home"
import Playlist from "./playlist";
import SongList from "./songlist";
import "../styles/App.css"

function App() {

  const userId = 1
  // const [userId, setUserId] = useState(1)
  const [playlistId, setPlaylistId] = useState()
  const [currentPlaylist, setCurrentPlaylist] = useState()
  const [playlists, setPlaylists] = useState([])

  return (
    <div className="App-container">
      <Sidebar playlists={playlists} setPlaylists={setPlaylists} setCurrentPlaylist={setCurrentPlaylist} userId={userId}/>
      <div className="main-content-container">
        <Switch>

        {/* Route home */}
          <Route exact path="/">
            <Home />  
          </Route>

        {/* Route all songs */}
          <Route exact path="/songs">
            <SongList />
          </Route>

        {/* Route selected playlist */}
          <Route path="/playlist">
            <Playlist playlists={playlists}playlistId={playlistId} userId={userId} currentPlaylist={currentPlaylist}/>
          </Route>

        </Switch>
      </div>
    </div>
  );
}

export default App;
